import React from 'react';

// Import Chakra components you need
import { Box, Image, Heading, Text, VStack } from '@chakra-ui/react';

const ProfileCard = ({ name, role, image, description }) => {
  return (
    // 'Box' is a generic div from Chakra UI
    <Box
      maxW="sm" // max-width
      borderWidth="1px"
      borderRadius="lg" // large border-radius
      overflow="hidden"
      boxShadow="lg" // large box-shadow
      p={6} // padding
      textAlign="center"
      bg="white"
      _hover={{ transform: 'translateY(-5px)', boxShadow: 'xl' }} // hover effect
      transition="all 0.2s"
    >
      {/* VStack arranges items vertically with spacing */}
      <VStack spacing={4}>
        <Image
          borderRadius="full"
          boxSize="150px"
          src={image}
          alt={`${name}'s profile`}
          objectFit="cover"
          border="4px solid"
          borderColor="gray.200"
        />
        <Heading as="h2" size="lg">
          {name}
        </Heading>
        <Text color="blue.500" fontWeight="semibold">
          {role}
        </Text>
        <Text color="gray.600" fontSize="sm">
          {description}
        </Text>
      </VStack>
    </Box>
  );
};

export default ProfileCard;
