import React, { useState, useEffect } from 'react';
import { Box, Heading, SimpleGrid, Container } from '@chakra-ui/react';
import ProfileCard from './components/ProfileCard/ProfileCard';
import ProfileCardSkeleton from './components/ProfileCard/ProfileCardSkeleton'; // Import skeleton

// Mock user data (in a real app, this would come from an API)
const users = [
  {
    name: 'Alice Johnson',
    role: 'Frontend Developer',
    image: 'https://i.pravatar.cc/150?img=1',
    description: 'Passionate about creating beautiful and user-friendly web interfaces with React.',
  },
  {
    name: 'Bob Williams',
    role: 'Backend Developer',
    image: 'https://i.pravatar.cc/150?img=3',
    description: 'Expert in Node.js, databases, and building robust APIs that power our applications.',
  },
  {
    name: 'Charlie Brown',
    role: 'UI/UX Designer',
    image: 'https://i.pravatar.cc/150?img=5',
    description: 'Focuses on user experience and visual design. Turns complex problems into elegant solutions.',
  },
];

function App() {
  const [isLoading, setIsLoading] = useState(true);

  // Simulate loading data
  useEffect(() => {
    // This timeout simulates a network request
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2500); // Load for 2.5 seconds

    // Cleanup function to clear the timer
    return () => clearTimeout(timer);
  }, []); // The empty array means this effect runs only once

  return (
    <Box bg="gray.50" minH="100vh" py={10}>
      <Container maxW="container.lg">
        <Heading as="h1" textAlign="center" mb={10}>
          Meet Our Team âœ¨
        </Heading>
        {/* 'SimpleGrid' creates a responsive grid layout */}
        <SimpleGrid columns={{ base: 1, md: 2, lg: 3 }} spacing={8}>
          {isLoading
            ? // If loading, show 3 skeleton cards
              Array.from({ length: 3 }).map((_, index) => (
                <ProfileCardSkeleton key={index} />
              ))
            : // Otherwise, show the actual user cards
              users.map((user, index) => <ProfileCard key={index} {...user} />)}
        </SimpleGrid>
      </Container>
    </Box>
  );
}

export default App;
